import axios from 'axios';

axios.defaults.baseURL = "http://localhost:5068";

axios.interceptors.response.use(
    response => {
      response},
    error => {
      console.log(`ארעה שגיאה!!! קוד שגיאה: ${error.status} , הודעת שגיאה: ${error.response || error.massage}`)
      return Promise.reject(error);
    })



export default {
  getTasks: async () => {
    const result = await axios.get(`/tasks`)    
    return result.data;
  },

  addTask: async(name)=>{
    const result = await axios.post(`/tasks`,name,  {
      headers: {
          'Content-Type': 'text/plain', // מציין שה-Body הוא טקסט רגיל
      },
  });
    return result.status;
  },

  setCompleted: async(id, isComplete)=>{
    const result = await axios.put(`/tasks/${id}`)
    return {};
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`/tasks/${id}`);
    return result.status;
  }
};
