import axios from 'axios';

const apiUrl = "http://localhost:5068"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`)    
    return result.data;
  },

  addTask: async(name)=>{
    const result = await axios.post(`${apiUrl}/tasks`,name,  {
      headers: {
          'Content-Type': 'text/plain', // מציין שה-Body הוא טקסט רגיל
      },
  });
    return result.status;
  },

