import axios from 'axios';

const apiUrl = "http://localhost:5068"
axios.defaults.baseURL = "http://localhost:5068/tasks";

export default {
  getTasks: async () => {
    const result = await axios.get(`/tasks`)    
    return result.data;
  },

  addTask: async(name)=>{
    const result = await axios.post(`/tasks`,name,  {
      headers: {
          'Content-Type': 'text/plain', // מציין שה-Body הוא טקסט רגיל
      },
  });
    return result.status;
  },

  setCompleted: async(id, isComplete)=>{
    const result = await axios.put(`${apiUrl}/tasks/${id}`)
    return {};
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`${apiUrl}/tasks/${id}`);
    return result.status;
  }
};
