import axios from 'axios';
import jwt_decode, { jwtDecode } from 'jwt-decode';

axios.defaults.baseURL = "http://localhost:5068";
setAuthorizationBearer()

function saveAccessToken(authResult){
  localStorage.setItem("access_token", authResult.token)
  setAuthorizationBearer()
}

function setAuthorizationBearer(){
  const myToken = localStorage.getItem("access_token")
  if(myToken) 
    axios.defaults.headers.common["Authorization"] = `Bearer ${myToken}`
}

axios.interceptors.response.use(
    response => {
      return response},
    error => {
      if (error.status == 401 && window.location.pathname !== '/login') {
        window.location.href = '/login';  // דרך ישירה להפנות לדף login
      }
      else{
        console.log(`ארעה שגיאה!!! קוד שגיאה: ${error.status} , הודעת שגיאה: ${error.response.massage || error.massage}`)
        return Promise.reject(error);  // תחזיר את השגיאה כדי שהקוד יוכל להמשיך לטפל בה אם צריך
      }
    })



export default {
  logout: () => {
    localStorage.setItem("access_token","")
  },

  login: async (userName, password) => {
    await setAuthorizationBearer();  // הוסף כאן את הקריאה לפני קריאת ה-login
    var d = 0
    const res = await axios.post("/login", {id:d,nameUser:userName, password:password})
    console.log("Login Response:", res.data);  
    console.log("Token from response:", res.data.token);
    saveAccessToken(res.data.token)
  },

  register: async (userName, password) => {
    var d = 0
    alert(userName+" "+password)
    const res = await axios.post("/registr",{id:d,NameUser:userName, password:password})
    console.log("Login Response:", res.data);  
    saveAccessToken(res.data.token)
  },

  getLoginUser: () => {
    const token = localStorage.getItem("access_token")
    if(token)
      return jwtDecode(token)
    return null
  },

  getTasks: async () => {    
    alert("2")
    const token = localStorage.getItem("access_token");
    console.log("Token:", token);
    const result = await axios.get("/tasks", {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }); 
    alert("3")
     return result.data;
  },

  addTask: async(name)=>{
    const result = await axios.post(`/tasks?newTask=${encodeURIComponent(name)}`);
    return result.status;
  },

  setCompleted: async(id, isComplete)=>{
    const result = await axios.put(`/tasks/${id}`)
    return {};
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`/tasks/${id}`);
    return result.status;
  }
};
