import React, { useState } from 'react';
import service from './service.js'; // מייבא את השירות שלך

function Login() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  
    // 🔹 אם המשתמש כבר מחובר, נשלח אותו לדף הבית
    useEffect(() => {
      const user = service.getLoginUser();
      if (user != null) {
        window.location.href = "/"; 
      }
    }, []);
    
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      alert("Ssss")
      await service.login(userName, password);  // הפונקציה הזו שולחת את פרטי ההתחברות לשרת ומבצע את ה-login
      window.location.href = "/"; // אם ההתחברות הצליחה, נעבור לדף הבית (או לעמוד אחר שאתה מעוניין בו)
    } catch (e) {
      setError("שם משתמש או סיסמה לא נכונים.");
    }
  }

  return (
    <div>
      <h2>התחברות</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="שם משתמש" 
          value={userName} 
          onChange={(e) => setUserName(e.target.value)} 
        />
        <input 
          type="password" 
          placeholder="סיסמה" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type="submit">התחבר</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
}

export default Login;
